  created() {
    this.intData = {
      title: 'Explore Our Services',
      services: [...this.defaultServices]
    };
  },
  methods: {
    editButton(i) {
      this.selectedButton = i;
      setTimeout(() => this.$refs[`linkInput-${i}`][0].focus(), 0);
    },
    deleteButton(i) {
      this.$delete(this.intData.buttons, i);
    },
    addButton() {
      this.intData.buttons.push({});
    },
    onDragEnd() {
      this.drag = false;
      this.onChange();
    },
    remove() {
      this.$emit('remove');
    },
    onChange() {
      this.$emit('onChange', this.intData);
    },
    updateAssociatedLocations(val) {
      this.$emit('updateAssociatedLocations', val);
    },
    updateHidden(val) {
      this.$emit('updateHidden', val);
    },
    debounceOnChange: debounce(function () {
      this.onChange();
    }, 1500),
  }
};
