    <div class="h-100" v-else>
        <div class="settings-head p-4 border-bottom d-flex align-items-center justify-content-between">
          <h5 class="font-weight-bold m-0">Google Merchant Center Account Setup</h5>
          <a href="javascript:void(0)" @click="accountType = ''" v-if="!account.id && accountType" class="text-primary mr-2">
            <i class="fa fa-chevron-left"></i>
            Back
          </a>
